import React, { useState } from 'react'
import { useRouter } from 'next/router'
const CreateGens = () => {
    const route = useRouter();
    var gens = [];

    const [disableTypeP, setDisableTypeP] = useState(true)
    const [disableTypeY, setDisableTypeY] = useState(true)
    const { gen, setGen } = useState({
        id: route.query.createGenCompetId,
        type: "",
        gen: ""
    })
    var count = 0;
    const handleCheckType = e => {
        if (e.target.value === "ประชาชน") {
            if (e.target.checked == false) {
                setDisableTypeP(true)
            }
            else {
                setDisableTypeP(false)
            }
        }
        else {
            if (e.target.checked == false) {
                setDisableTypeY(true)
            }
            else {
                setDisableTypeY(false)
            }
        }
    }
    const handleCheckGen = e => {
        if (e.target.name === "genP") {
            if (disableTypeP === false) {
                gens.push({ name: "ประชาชน", gen: e.target.value })
            }

        }
        else {
            if (disableTypeY === false) {
                gens.push({ name: "เยาวชน", gen: e.target.value })
            }
        }
        //gens.save()
        console.log(gens);

    }
    return (
        <div style={{ margin: 80 }}>
            Create {route.query.createGenCompetId}
            <form action="">
                <label>
                    <input type="checkbox" name="type[]" value="ประชาชน" onChange={handleCheckType} />
                    ประชาชน
                </label>
                <label>
                    <input type="checkbox" name="genP[]" disabled={disableTypeP} value="N" onChange={handleCheckGen} />
                    N
                </label>
                <label>
                    <input type="checkbox" name="genP[]" disabled={disableTypeP} value="S+" onChange={handleCheckGen} />
                    S+
                </label>
                <label>
                    <input type="checkbox" name="genP[]" disabled={disableTypeP} value="S-" onChange={handleCheckGen} />
                    S-
                </label>
                <label>
                    <input type="checkbox" name="genP[]" disabled={disableTypeP} value="P-" onChange={handleCheckGen} />
                    P-
                </label>
                <label>
                    <input type="checkbox" name="genP[]" disabled={disableTypeP} value="P+C" onChange={handleCheckGen} />
                    P+C
                </label>
                <br />
                <label>
                    <input type="checkbox" name="type[]" value="เยาวชน" onChange={handleCheckType} />
                    เยาวชน
                </label>
                <label>
                    <input type="checkbox" name="genY[]" disabled={disableTypeY} value="U9" onChange={handleCheckGen} />
                    U9
                </label>
                <label>
                    <input type="checkbox" name="genY[]" disabled={disableTypeY} value="U11" onChange={handleCheckGen} />
                    U11
                </label>
                <label>
                    <input type="checkbox" name="genY[]" disabled={disableTypeY} value="U13" onChange={handleCheckGen} />
                    U13
                </label>
                <label>
                    <input type="checkbox" name="genY[]" disabled={disableTypeY} value="U15" onChange={handleCheckGen} />
                    U15
                </label>
                <label>
                    <input type="checkbox" name="genY[]" disabled={disableTypeY} value="U17" onChange={handleCheckGen} />
                    U17
                </label>
                <button type="submit">click</button>
            </form>
        </div >
    )
}
export default CreateGens
