import React, { useState } from 'react'
import { useRouter } from 'next/router'
import gql from 'graphql-tag'
import { useMutation } from '@apollo/react-hooks'
import apolloClient from '../../../apollo/apolloClient'

const CREATE_GEN = gql`
mutation CREATE_GEN($id: ID!,$type:String!,$gen:String){
    createCompetition_gen(
        id : $id,
        type: $type,
        gen: $gen
  ){
        type
        gen 
        compet_id{
        id
        name
        }
  }
}

`




const CreateGens = () => {
    const route = useRouter();
    const [disableGenP, setDisableGenP] = useState(true)
    const [disableGenY, setDisableGenY] = useState(true)
    const [genInfo, setGen] = useState({
        id: route.query.createGenCompetId,
        type: "",
        gen: ""
    })
    const [success, setSuccess] = useState(false)

    const [createGen, { error, loading, data }] = useMutation(CREATE_GEN, {
        variables: { ...genInfo },
        onCompleted: data => {
            if (data) {
                setSuccess(true)
                setGen({
                    id: route.query.createGenCompetId,
                    type: "",
                    gen: ""
                })
            }
        }
    })

    const handleSave = async e => {
        try {
            e.preventDefault()
            await createGen()

        } catch (error) {
            console.log(error);
        }
    }

    const handleChange = e => {

        setGen({
            ...genInfo,
            [e.target.name]: e.target.value
        })
        console.log(genInfo);
    }

    const handleType = e => {
        if (e.target.value == "ประชาชน") {
            setDisableGenP(false)
            setDisableGenY(true)
        }
        else if (e.target.value == "เยาวชน") {
            setDisableGenP(true)
            setDisableGenY(false)
        }
        else if (e.target.value == null) {
            setDisableGenP(true)
            setDisableGenY(true)
        }
        setGen({
            ...genInfo,
            [e.target.name]: e.target.value
        })
        //console.log(genInfo);

    }

    const selectGen = () => {
        if (disableGenP && disableGenY) {
            return (<p>กรุณาเลือกประเภท</p>);
        }
        else if (disableGenP == false && disableGenY == true) {
            return (
                <select name="gen" onChange={handleChange}>
                    <option disabled={disableGenP} value={null}>เลือกรุ่น</option>
                    <option disabled={disableGenP} value="N">N</option>
                    <option disabled={disableGenP} value="S+">S+</option>
                    <option disabled={disableGenP} value="S-">S-</option>
                    <option disabled={disableGenP} value="P-">P-</option>
                    <option disabled={disableGenP} value="P+C">P+C</option>
                </select>
            )
        }
        else {
            return (
                <select name="gen" onChange={handleChange}>
                    <option disabled={disableGenP} value={null}>เลือกรุ่น</option>
                    <option disabled={disableGenY} value="U9">U9</option>
                    <option disabled={disableGenY} value="U11">U11</option>
                    <option disabled={disableGenY} value="U13">U13</option>
                    <option disabled={disableGenY} value="U15">U15</option>
                    <option disabled={disableGenY} value="U17">U17</option>
                </select>
            )
        }
    }


    return (
        <div style={{ margin: 80 }}>
            {/* //Create {} */}
            <form onSubmit={handleSave}>
                <select name="type" onChange={handleType}>
                    <option value={null}>กรุณาเลือก</option>
                    <option value="ประชาชน">ประชาชน</option>
                    <option value="เยาวชน">เยาวชน</option>
                </select>
                {selectGen()}
                <button type="submit" style={{ margin: '5px', padding: '18px' }} s>บันทึก</button>
            </form>
            <div>
                {success && <p>You successfully Create Competitions,please create Gens <Link href="../Competitions/"><a >Competitions.</a></Link></p>}

                {/* {error && <p style={{ color: 'red' }}>{error.graphQLErrors[0].message}</p>} */}
            </div>
        </div >
    )
}
export default apolloClient(CreateGens)
