import React, { useState } from 'react'
import { useRouter } from 'next/router'
const CreateGens = () => {
    const route = useRouter();
    const [disableGenP, setDisableGenP] = useState(true)
    const [disableGenY, setDisableGenY] = useState(true)
    //const [disableGenY, setDisableGenY] = useState(true)

    const handleType = e => {
        if (e.target.value == "ประชาชน") {
            setDisableGenP(false)
            setDisableGenY(true)
        }
        else if (e.target.value == "เยาวชน") {
            setDisableGenP(true)
            setDisableGenY(false)
        }
        else {
            setDisableGenP(true)
            setDisableGenY(true)
        }
    }

    const selectGen = () => {
        if (disableGenP && disableGenY) {
            return (<p>กรุณาเลือกประเภท</p>);
        }
        else if (disableGenP == false && disableGenY == true) {
            return (
                <select>
                    <option name="gen" disabled={disableGenP} value="N">N</option>
                    <option name="gen" disabled={disableGenP} value="S+">S+</option>
                    <option name="gen" disabled={disableGenP} value="S-">S-</option>
                    <option name="gen" disabled={disableGenP} value="P-">P-</option>
                    <option name="gen" disabled={disableGenP} value="P+C">P+C</option>
                </select>
            )
        }
    }
    return (
        <div style={{ margin: 80 }}>
            Create {route.query.createGenCompetId}
            <form action="" >
                <select onChange={handleType}>
                    <option name="type" value={null}>กรุณาเลือก</option>
                    <option name="type" value="ประชาชน">ประชาชน</option>
                    <option name="type" value="เยาวชน">เยาวชน</option>
                </select>
                {

                }
            </form>
            {/* <select>
                    <option name="gen" disabled={disableGenP} value="N">N</option>
                    <option name="gen" disabled={disableGenP} value="S+">S+</option>
                    <option name="gen" disabled={disableGenP} value="S-">S-</option>
                    <option name="gen" disabled={disableGenP} value="P-">P-</option>
                    <option name="gen" disabled={disableGenP} value="P+C">P+C</option>
                    <option name="gen" disabled={disableGenY} value="U9">U9</option>
                    <option name="gen" disabled={disableGenY} value="U11">U11</option>
                    <option name="gen" disabled={disableGenY} value="U13">U13</option>
                    <option name="gen" disabled={disableGenY} value="U15">U15</option>
                    <option name="gen" disabled={disableGenY} value="U17">U17</option>
                </select> */}

        </div >
    )
}
export default CreateGens
