import React, { useState } from 'react'
import { useRouter } from 'next/router'
const CreateGens = () => {
    const route = useRouter();
    var gens = [];
    var typeTemp;

    const [disableType, setDisableType] = useState(true)
    const { gen, setGen } = useState({
        id: route.query.createGenCompetId,
        type: "",
        gen: ""
    })
    var count = 0;
    const handleCheckType1 = e => {
        if (e.target.value === "ประชาชน") {
            typeTemp = e.target.value;
            if (e.target.checked == false) {
                setDisableType(true)
            }
            else {
                setDisableType(false)
                typeTemp = e.target.value
            }
        }
        else {
            var gen = { name: typeTemp, gen: e.target.value }
            if (e.target.checked == true) {
                count++;
                gens.push(gen)
            }
            else {
                count--;
                gens.pop(e.target.value)
            }
        }
    }
    return (
        <div style={{ margin: 80 }}>
            Create {route.query.createGenCompetId}
            <form action="">
                <label>
                    <input type="checkbox" name="type" value="ประชาชน" onChange={handleCheckType1} />
                    ประชาชน
                </label>
                <label>
                    <input type="checkbox" name="gen" disabled={disableType} value="N" onChange={handleCheckType1} />
                    N
                </label>
                <label>
                    <input type="checkbox" name="gen" disabled={disableType} value="S+" onChange={handleCheckType1} />
                    S+
                </label>
                <label>
                    <input type="checkbox" name="gen" disabled={disableType} value="S-" onChange={handleCheckType1} />
                    S-
                </label>
                <label>
                    <input type="checkbox" name="gen" disabled={disableType} value="P-" onChange={handleCheckType1} />
                    P-
                </label>
                <label>
                    <input type="checkbox" name="gen" disabled={disableType} value="P+C" onChange={handleCheckType1} />
                    P+C
                </label>
                <br />
                {/* <label>
                    <input type="checkbox" name="type" value="เยาวชน" onChange={handleCheckType2} />
                    เยาวชน
                </label>
                <label>
                    <input type="checkbox" name="gen" value="U9" onChange={handleCheckType2} />
                    U9
                </label>
                <label>
                    <input type="checkbox" name="gen" value="U11" onChange={handleCheckType2} />
                    U11
                </label>
                <label>
                    <input type="checkbox" name="gen" value="U13" onChange={handleCheckType2} />
                    U13
                </label>
                <label>
                    <input type="checkbox" name="gen" value="U15" onChange={handleCheckType2} />
                    U15
                </label>
                <label>
                    <input type="checkbox" name="gen" value="U17" onChange={handleCheckType2} />
                    U17
                </label> */}
            </form>
        </div>
    )
}
export default CreateGens
