import React from 'react'
import Link from 'next/link'
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import Card from 'react-bootstrap/Card'
import ScrollAnimation from 'react-animate-on-scroll';
import { bounce } from 'react-animations';
import styled, { keyframes } from 'styled-components'
import { useQuery } from "@apollo/react-hooks"
import gql from 'graphql-tag'


const fakeData = [
    {
        id: 1,
        name: "Walailak Day",
        detail: "รายละเอียด.",
        place: "สนามกีฬา1",
        price: 200,
        compet_start: "2020-01-01",
        compet_end: "2020-01-01",
        start: "2020-01-01",
        end: "2020-01-01",
        pay_end: "2020-01-01",
        //gens: [Competition_Gen]
    },
    {
        id: 2,
        name: "Walailak Day2",
        detail: "รายละเอียด..",
        place: "สนามกีฬา2",
        price: 300,
        compet_start: "2020-01-01",
        compet_end: "2020-01-01",
        start: "2020-01-01",
        end: "2020-01-01",
        pay_end: "2020-01-01",
        //gens: [Competition_Gen]
    },
    {
        id: 3,
        name: "Walailak Day3",
        detail: "รายละเอียด...",
        place: "สนามกีฬา3",
        price: 600,
        compet_start: "2020-01-01",
        compet_end: "2020-01-01",
        start: "2020-01-01",
        end: "2020-01-01",
        pay_end: "2020-01-01",
        //gens: [Competition_Gen]
    }
]

const QUERY_COMPETITIONS = gql`
    query{
        competitions{
            id
            name
            detail
            place
            gens{
                id
                gen
            }
        }
    }
`

const img = require("../back.jpg");
function Competition() {
    const { data, loading, error } = useQuery(QUERY_COMPETITIONS)

    if (error) return <p>Ooobs...somthing went wrong,please try again later.</p>

    if (loading)
        // const result = useQuery(QUERY_COMPETITIONS,{id :'aa'}) กรณีที่ต้องใส่ args
        return (
            <div>
                <div className="team" style={{ padding: 80 }}>
                    <Container fluid style={{ padding: 80 }}>
                        <Container >
                            <Row >
                                <ScrollAnimation animateIn='fadeIn'
                                    animateOut='fadeOut'>
                                    <Row className="justify-content-md-center" >
                                        <Col md={6} sm={6} lg={6} style={Title} ><Bounce>Badminton</Bounce></Col>
                                        <Col></Col>
                                    </Row>
                                    <Row className="justify-content-md-center" >
                                        <Col> </Col>
                                        <Col md={8} sm={8} lg={8} style={Title}><a style={{ backgroundColor: "white", color: "black" }}>Com</a><a style={{ backgroundColor: "black" }}>petitions</a></Col>
                                    </Row>

                                </ScrollAnimation>
                            </Row>
                        </Container>
                    </Container>
                </div>
                <Container >

                    {fakeData.map(prod => (
                        <ScrollAnimation animateIn='flipInY'>
                            <Card style={card}>
                                <Card.Header style={{ fontSize: 20 }}>ชื่อการแข่งขัน {prod.name}</Card.Header>
                                <Card.Body>
                                    <Card.Title>สถานที่จัดแข่งคือ {prod.place}</Card.Title>
                                    <Card.Text>
                                        วันเปิดรับสมัคร : {prod.start} วันสิ้นสุดรับสมัคร : {prod.end}
                                    </Card.Text>
                                    <Link key={prod.id} href='/Competitions/[competitionId]' as={`/Competitions/${prod.id}`} >
                                        <a style={Detail}> รายละเอียด เพิ่มเติม...</a>
                                    </Link>
                                </Card.Body>
                            </Card>
                        </ScrollAnimation>

                    ))}
                </Container>
                <style JSX>{`
                .team {
                background: url('`+ img + `');
                height: 100%;
                background-position: center;
                background-repeat: no-repeat;
                background-size: cover;
                }
                `}
                </style>
            </div >


        )
}

// const divstyle = {
//     display: 'grid',
//     justifyContent: 'center'
// }np
const Bounce = styled.div`animation: 2s ${keyframes`${bounce}`} infinite`;

const card = {
    border: "outset",
    padding: 10,
    margin: 30,
}

const Title = {
    fontSize: 120,
    color: "white"
}


const Detail = {
    fontSize: 15
}

export default Competition;
