import React, { useState } from 'react'
import { useRouter } from 'next/router'
const CreateGens = () => {
    const route = useRouter();
    var gens = [];

    const [disableTypeP, setDisableTypeP] = useState(true)
    const [disableTypeY, setDisableTypeY] = useState(true)
    const { gen, setGen } = useState({
        id: route.query.createGenCompetId,
        type: "",
        gen: ""
    })
    var count = 0;
    const handleCheckType = e => {
        if (e.target.value === "ประชาชน") {
            if (e.target.checked == false) {
                setDisableTypeP(true)
            }
            else {
                setDisableTypeP(false)
            }
        }
        else {
            if (e.target.checked == false) {
                setDisableTypeY(true)
            }
            else {
                setDisableTypeY(false)
            }
        }
    }
    const handleCheckGen = e => {
        if (e.target.value === "เยาวชน") {
            if (e.target.checked == false) {
                setDisableTypeY(true)
            }
            else {
                setDisableTypeY(false)
            }
        }
        else {
            var gen = { name: "เยาวชน", gen: e.target.value }
            if (e.target.checked == true) {
                count++;
                gens.push(gen)
            }
            else {
                count--;
                gens.pop(gen)
            }
            console.log(count);

        }
    }
    return (
        <div style={{ margin: 80 }}>
            Create {route.query.createGenCompetId}
            <form action="">
                <label>
                    <input type="checkbox" name="type" value="ประชาชน" onChange={handleCheckType} />
                    ประชาชน
                </label>
                <label>
                    <input type="checkbox" name="genP" disabled={disableTypeP} value="N" onChange={handleCheckType1} />
                    N
                </label>
                <label>
                    <input type="checkbox" name="genP" disabled={disableTypeP} value="S+" onChange={handleCheckType1} />
                    S+
                </label>
                <label>
                    <input type="checkbox" name="genP" disabled={disableTypeP} value="S-" onChange={handleCheckType1} />
                    S-
                </label>
                <label>
                    <input type="checkbox" name="genP" disabled={disableTypeP} value="P-" onChange={handleCheckType1} />
                    P-
                </label>
                <label>
                    <input type="checkbox" name="genP" disabled={disableTypeP} value="P+C" onChange={handleCheckType1} />
                    P+C
                </label>
                <br />
                <label>
                    <input type="checkbox" name="type" value="เยาวชน" onChange={handleCheckType} />
                    เยาวชน
                </label>
                <label>
                    <input type="checkbox" name="genY" disabled={disableTypeY} value="U9" onChange={handleCheckType2} />
                    U9
                </label>
                <label>
                    <input type="checkbox" name="genY" disabled={disableTypeY} value="U11" onChange={handleCheckType2} />
                    U11
                </label>
                <label>
                    <input type="checkbox" name="genY" disabled={disableTypeY} value="U13" onChange={handleCheckType2} />
                    U13
                </label>
                <label>
                    <input type="checkbox" name="genY" disabled={disableTypeY} value="U15" onChange={handleCheckType2} />
                    U15
                </label>
                <label>
                    <input type="checkbox" name="genY" disabled={disableTypeY} value="U17" onChange={handleCheckType2} />
                    U17
                </label>
            </form>
        </div>
    )
}
export default CreateGens
