import React, { useState } from 'react'
import { useRouter } from 'next/router'
const CreateGens = () => {
    const route = useRouter();
    var gen1 = [];
    var gen2 = [];
    // const [checking, setChecking] = useState(false)
    const { gen, setGen } = useState({
        id: route.query.createGenCompetId,
        type: "",
        gen: ""
    })
    var count = 0;
    const handleCheckType1 = e => {
        if (e.target.checked == true) {
            count++;
            gen1.push(e.target.value)
        }
        else {
            count--;
            gen1.pop(e.target.value)
        }
    }
    const handleCheckType2 = e => {
        if (e.target.checked == true) {
            count++;
            gen2.push(e.target.value)
        }
        else {
            count--;
            gen2.pop(e.target.value)
        }
    }
    return (
        <div style={{ margin: 80 }}>
            Create {route.query.createGenCompetId}
            <form action="">
                <label>
                    <input type="checkbox" name="type" value="ประชาชน" onChange={handleCheckType1} />
                    ประชาชน
                </label>
                <label>
                    <input type="checkbox" name="gen" value="N" onChange={handleCheckType1} />
                    N
                </label>
                <label>
                    <input type="checkbox" name="gen" value="S+" onChange={handleCheckType1} />
                    S+
                </label>
                <label>
                    <input type="checkbox" name="gen" value="S-" onChange={handleCheckType1} />
                    S-
                </label>
                <label>
                    <input type="checkbox" name="gen" value="P-" onChange={handleCheckType1} />
                    P-
                </label>
                <label>
                    <input type="checkbox" name="gen" value="P+C" onChange={handleCheckType1} />
                    P+C
                </label>
                <br />
                <label>
                    <input type="checkbox" name="type" value="เยาวชน" onChange={handleCheckType1} />
                    ประชาชน
                </label>
                <label>
                    <input type="checkbox" name="gen" value="U9" onChange={handleCheckType1} />
                    N
                </label>
                <label>
                    <input type="checkbox" name="gen" value="U11" onChange={handleCheckType1} />
                    S+
                </label>
                <label>
                    <input type="checkbox" name="gen" value="U13" onChange={handleCheckType1} />
                    S-
                </label>
                <label>
                    <input type="checkbox" name="gen" value="U15" onChange={handleCheckType1} />
                    P-
                </label>
                <label>
                    <input type="checkbox" name="gen" value="P+C" onChange={handleCheckType1} />
                    P+C
                </label>
            </form>
        </div>
    )
}
export default CreateGens
