import React from 'react'
import Link from 'next/link'
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'


const fakeData = [
    {
        id: 1,
        name: "Walailak Day",
        detail: "รายละเอียด.",
        place: "สนามกีฬา1",
        price: 200,
        compet_start: "2020-01-01",
        compet_end: "2020-01-01",
        start: "2020-01-01",
        end: "2020-01-01",
        pay_end: "2020-01-01",
        //gens: [Competition_Gen]
    },
    {
        id: 2,
        name: "Walailak Day2",
        detail: "รายละเอียด..",
        place: "สนามกีฬา2",
        price: 300,
        compet_start: "2020-01-01",
        compet_end: "2020-01-01",
        start: "2020-01-01",
        end: "2020-01-01",
        pay_end: "2020-01-01",
        //gens: [Competition_Gen]
    },
    {
        id: 3,
        name: "Walailak Day3",
        detail: "รายละเอียด...",
        place: "สนามกีฬา3",
        price: 600,
        compet_start: "2020-01-01",
        compet_end: "2020-01-01",
        start: "2020-01-01",
        end: "2020-01-01",
        pay_end: "2020-01-01",
        //gens: [Competition_Gen]
    }
]

function Competition() {
    return (
        <Container >
            {fakeData.map(prod => (
                <Row className="justify-content-md-center" style={card}>
                    <Col md={6} sm={6} lg={6} >
                        <Link key={prod.id} href='/Competitions/[competitionId]' as={`/Competitions/${prod.id}`} >
                            {prod.name}
                        </Link>
                    </Col>
                    <Col md={6} sm={6} lg={6} >
                        <Row>
                            สถานที่ : {prod.place}
                        </Row>
                        <Row>
                            ค่าสมัคร : {prod.price}
                        </Row>
                        <Row>
                            วันสิ้นสุดจ่ายเงิน : {prod.pay_end}
                        </Row>
                        <Row>
                            วันรับสมัคร : {prod.start}
                        </Row>
                        <Row>
                            วันสิ้นสุดรับสมัคร : {prod.end}
                        </Row>
                        <Row>
                            วันเริ่มการแข่งขัน : {prod.compet_start}
                        </Row>
                        <Row>
                            วันสิ้นสุดการแข่งขัน : {prod.compet_end}
                        </Row>

                    </Col>
                </Row>
            ))}
        </Container>
    )
}

const divstyle = {
    display: 'grid',
    justifyContent: 'center'
}

const card = {
    border: "outset",
    padding: 10,
    margin: 10
}
export default Competition;
