import React from 'react'
import { useRouter } from 'next/router'
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import Card from 'react-bootstrap/Card'
import ScrollAnimation from 'react-animate-on-scroll';
import Link from 'next/link'
import gql from 'graphql-tag'

const QUERY_COMPETITIONS = gql`
    query{
        competitions{
            id
            name
            detail
            place
            price
            compet_start
            compet_end
            start
            end
            pay_end
            gens{
                id
                gen
                type
            }
        }
    }
`

const competitionId = () => {
    const route = useRouter()

    return (
        // <div>
        //     {route.query.competitionId}
        // </div>
        <Container>
            <ScrollAnimation animateIn='jackInTheBox'
                animateOut='fadeOut'>
                <Card className="text-center" style={{ margin: 80 }}>
                    <Card.Header>{route.query.competitionId}</Card.Header>
                    <Card.Body>
                        <Card.Title>Special title treatment</Card.Title>
                        <Card.Text>
                            With supporting text below as a natural lead-in to additional content.
                    </Card.Text>
                        <Link href='/Competitions'>
                            <a style={Detail}>กลับ</a>
                        </Link>
                    </Card.Body>
                    <Card.Footer className="text-muted">2 days ago</Card.Footer>
                </Card>
            </ScrollAnimation>
        </Container>
    )
}

const Detail = {
    fontSize: 15
}

export default competitionId
