import React from 'react'
import { useRouter } from 'next/router'
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import Card from 'react-bootstrap/Card'
import ScrollAnimation from 'react-animate-on-scroll';
import Link from 'next/link'
import gql from 'graphql-tag'
import apolloClient from '../../apollo/apolloClient'
import { useQuery } from '@apollo/react-hooks'

const QUERY_COMPETITION = gql`
    query QUERY_COMPETITION($id: ID!){
        competition(id: $id){
            id
            name
            detail
            place
            price
            compet_start
            compet_end
            start
            end
            pay_end
            gens{
            id
            type
            gen
            }
        }
    }
`

const Competition = () => {
    const route = useRouter();
    const { data, loading, error } = useQuery(QUERY_COMPETITION, {
        variables: { id: route.query.competitionId }
    })

    if (error) return <a>Somethings went wrong,please try agein.</a>
    if (loading) return <a>loading....</a>

    return (
        // <div>
        //     {route.query.competitionId}
        // </div>
        <Container>
            <ScrollAnimation animateIn='jackInTheBox'
                animateOut='fadeOut'>
                <Card className="text-center" style={{ margin: 80 }}>
                    <Card.Header>{data.competition.name}</Card.Header>
                    <Card.Body>
                        <Card.Title>Special title treatment</Card.Title>
                        <Card.Text>
                            {data.competition.detail}
                        </Card.Text>
                        <Link href='/Competitions'>
                            <a style={Detail}>กลับ</a>
                        </Link>
                    </Card.Body>
                    <Card.Footer className="text-muted">2 days ago</Card.Footer>
                </Card>
            </ScrollAnimation>
        </Container>
    )
}

const Detail = {
    fontSize: 15
}

export default apolloClient(Competition)
